import React from 'react';
import { render } from 'react-dom';
import { setConfiguration } from 'react-grid-system';
import { Container, Row, Col } from 'react-grid-system';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql
} from "@apollo/client";
import { GraphQLID } from 'graphql';

setConfiguration({ maxScreenClass: 'xl' });

const client = new ApolloClient({
  uri: 'https://api.github.com/graphql',
  cache: new InMemoryCache(),
  headers: {
    Authorization: "Bearer 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"
  },
});

// const client content all requests found in the console
client
  .query({
    query: gql`
      query {
        viewer {
          login
          followers {
            totalCount
          }
          contributionsCollection {
            totalCommitContributions
          }
          repositories {
            totalCount
          }
          bio
          avatarUrl

          contributionsCollection {
            contributionYears
            totalRepositoryContributions
            endedAt
          }
          repositoriesContributedTo(first: 10) {
            totalCount
          }
        }        
      }
    `
  })
  .then(result => console.log(result));

const requests = gql`
  query {
    viewer { 
      login
    }
  }
`;

function GetUserName() {
  const { loading, error, data } = useQuery(requests);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <div>
      <p>
        {data.viewer.login}
      </p>
    </div>
  );
}


function App() {
  const container_Style = {
    border: "solid",
    marginBottom: 10,
    display: "grid",
  };
  const item_container_Style = {
    border: "solid"
  };
  const cols_style = {
    border:"solid",
    backgroundColor: 'grey',
  };


  return (
    <div>
      {/*----------------------------------------------Entête---------------------------------------------------------------------------*/}
      <Container style={{border:"solid", marginBottom:10}}>
        <Row style={{border:"solid", margin:10, padding:10}}>
          Informations générale (première ligne)
          <Col sm={2} style={cols_style}>
            Avatar (Les contours devront etre arrondis)
          </Col>
          <Col sm={2} style={cols_style}>
            Commits (le nombre de commit)
          </Col>
          <Col sm={2} style={cols_style}>
            Repos (Le nombre total de repository)
          </Col>
          <Col sm={2} style={cols_style}>
            Lines of Code (Le nombre total de repository)
          </Col>
        </Row>
      </Container>

      <div style={container_Style}>
        Share your profile on :
        <span>
          <a href="ideal link">aedin</a>
        </span>
        <span>
          <a href="ideal link">Twitter</a>
        </span>
        <span>
          <a href="ideal link">Facebook</a>
        </span>
        <span>
          <a href="ideal link">Get Html</a>
        </span>
      </div>

      {/*----------------------------------------------Les informatiionns générales--------------------------------------------------------*/}

      <div style={container_Style}>
        <div style={item_container_Style}>
          <h2><GetUserName /></h2>
          <div>La bio</div>
        </div>

        <div style={item_container_Style}>
          Les infos principales !!!
          Avatar ( Dans une bulle), nombre de commits, nombre de repository, Line de code, followers, followings
        </div>

      </div>

      {/*----------------------------------------------Overview--------------------------------------------------------------------------*/}

      <div style={container_Style}>
        Overview!!!!
        Le nombre de repository sur une période données (par année): .
        Laste update:
        <div style={item_container_Style}>
          Un diagrame d'activité lors de la période choisie
        </div>
      </div>
      {/*----------------------------------------------Langages--------------------------------------------------------------------------*/}
      <div style={container_Style}>
        Les langages !!!!
        Afficher toues les langages utilisées.
        Par langage afficher le nombre de commits et le nombre de loc
        <div style={item_container_Style}>
          Un diagrame d'activité en fonction des langages
        </div>
      </div>

      {/*----------------------------------------------Repository------------------------------------------------------------------------*/}

    </div>
  );
}



render(
  <ApolloProvider client={client}>
    <App />
  </ApolloProvider>,
  document.getElementById('root'),
);
